mapper.select:
select * from ( select rownum as RNUM, A.* from ( select u.USERID, f.REFUND_PRICE,f.REFUNDBEC_SEQ,b.DETAIL,f.REFUND_CANCLE,f.COMPFLAG, f.REFUND_SEQ,p.PAY_SEQ,r.RESERVATION_SEQ,r.TICKET_SEQ,f.REFUND_CANCLE_OK,p.USEDMILEAGE from TBL_REFUND f join TBL_REFUNDBEC b on f.REFUNDBEC_SEQ = b.REFUNDBEC_SEQ join TBL_PAYMENT p on p.PAY_SEQ = f.PAY_SEQ join TBL_RESERVATION r on r.RESERVATION_SEQ = p.RESERVATION_SEQ join TBL_USER u on u.USERID = r.USERID
) A ) where RNUM>#{firstRecordIndex}
and RNUM<=#{firstRecordIndex}+#{recordCountPerPage}
