mapper.update:
update tbl_user
set email1=#{email1},email2=#{email2},zipcode=#{zipcode}, address=#{address},
address_Detail=#{addressDetail},mail_agreement=#{mailAgreement}
where userid=#{userid}

mapper.select:
select count(*) 
from tbl_user
where userid = #{userid}

mapper.update:
update tbl_user
set password=#{password}
where userid=#{userid}

update tbl_user
set logout_date=sysdate
where userid=#{userid}
