mapper.select:
select *
from
(
    select rownum as RNUM, A.*
    from 
    (	
    	select t.*,
		(select count(*) from TBL_DISCOUNT d where d.TICKET_SEQ=t.ticket_seq) "count"
		from TBL_TICKET t 
		
		order by ticket_seq desc
    ) A
) where RNUM>#{firstRecordIndex} 

and RNUM<=#{firstRecordIndex}+#{recordCountPerPage}
