mapper.select:
select *
from
(
    select rownum as RNUM, A.*
    from
    (
        SELECT r.*,p.pay_condition
		FROM tbl_reservation r join tbl_payment p
		on r.RESERVATION_SEQ = p.RESERVATION_SEQ 
		where p.PAY_CONDITION='Y' and res_date>=#{startDay}
		 and res_date < to_Date(#{endDay})+1
		
			and userid=#{customerId}
		
		order by r.reservation_seq desc
    )A
)
where RNUM > #{firstRecordIndex} 

and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

SELECT count(*)
FROM tbl_reservation r join tbl_payment p
on r.RESERVATION_SEQ = p.RESERVATION_SEQ 
where p.PAY_CONDITION='Y' and res_date>=#{startDay}
 and res_date < to_Date(#{endDay})+1

	and userid=#{customerId}

select * from ( select rownum as RNUM, A.* from ( SELECT r.*,e.COMPFLAG,e.refund_cancle,e.refund_cancle_ok,f.DETAIL FROM tbl_reservation r join tbl_payment p on r.RESERVATION_SEQ = p.RESERVATION_SEQ join tbl_refund e on p.PAY_SEQ=e.PAY_SEQ join tbl_refundbec f on e.REFUNDBEC_SEQ = f.REFUNDBEC_SEQ where p.PAY_CONDITION='C' and refund_cancle>=#{startDay} and refund_cancle < to_Date(#{endDay})+1
and userid=#{customerId}
order by r.reservation_seq desc )A ) where RNUM > #{firstRecordIndex}
and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}
