mapper.select:
select *
from
(
    select rownum as RNUM, A.*
    from 
    (	
    	select * from tbl_user where grade_seq!=6 and out_date is null
	    
    ) A
) where RNUM>#{firstRecordIndex} 

and RNUM<=#{firstRecordIndex}+#{recordCountPerPage}

select *
from
(
    select rownum as RNUM, A.*
    from 
    (	
    	select u.USERID,u.BIRTH,u.GENDER,u.OUT_DATE,r.OUT_REASON
		from tbl_user u join OUT_REASON r
		on u.OUT_REASON_NO=r.OUT_REASON_SEQ
		where grade_seq!=6 and out_date is not null
	    
    ) A
) where RNUM>#{firstRecordIndex2} 

and RNUM<=#{firstRecordIndex2}+#{recordCountPerPage}

select * from ( select rownum as RNUM, A.* from ( select i.INQUERY_SEQ,INQUERY_TITLE,i.REG_DATE,inquery_condition,case when i.userid is null then '비회원' else '회원' end "usertype" from TBL_INQUERY i left join TBL_INQANW a on i.INQUERY_SEQ = a.INQUERY_SEQ where inquery_condition like '%'|| #{searchKeyword} ||'%'
order by inquery_seq desc ) A ) where RNUM>#{firstRecordIndex}
and RNUM<=#{firstRecordIndex}+#{recordCountPerPage}

select * from ( select rownum as RNUM, A.* from ( select * from ( select u.USERID, r.PAY_PRICE, r.REs_DATE, r.PRFNM,case when f.COMPFLAG is null then 'N' when f.compflag='N' then 'ing' else 'Y' end refYN from TBL_USER u left join TBL_RESERVATION r on u.USERID = r.USERID left join TBL_PAYMENT p on p.RESERVATION_SEQ = r.RESERVATION_SEQ left join TBL_REFUND f on f.PAY_SEQ = p.PAY_SEQ ) where userid=#{userid}
) A ) where RNUM>#{firstRecordIndex}
and RNUM<=#{firstRecordIndex}+#{recordCountPerPage}

select sum(p.PAY_PRICE) "totalpay"
from TBL_RESERVATION r join TBL_PAYMENT p
on r.RESERVATION_SEQ = p.RESERVATION_SEQ where r.USERID=#{userid}
