mapper.select:
select *
from
(
    select rownum as RNUM, A.*
    from 
    (	
    	select * from TBL_ANN where ann_type='1'
	    
		order by ann_seq desc
    ) A
) where RNUM>#{firstRecordIndex} 

and RNUM<=#{firstRecordIndex}+#{recordCountPerPage}

select *
from
(
    select rownum as RNUM, A.*
    from 
    (	
    	select * from TBL_ANN where ann_type='2'
	    
		order by ann_seq desc
    ) A
) where RNUM>#{firstRecordIndex} 

and RNUM<=#{firstRecordIndex}+#{recordCountPerPage}
