mapper.select:
select *
from
(
    select rownum as RNUM, A.*
    from
    (
        select likecount_seq,prfnm,genre,mt20id,(select count(*)
		from tbl_like b
		where b.mt20id=a.mt20id) LA
		from tbl_like a
		where userid=#{userid}
		order by likecount_seq desc
    )A
)
where RNUM > #{firstRecordIndex} 

and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}
